// src/components/ContactBrokers.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const ContactBrokers = () => {
  const [brokers, setBrokers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/api/brokers")
      .then(res => {
        setBrokers(res.data || []);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching brokers:", err);
        setError("Failed to load brokers. Please try again later.");
        setLoading(false);
      });
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-red-50 p-8">
      <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">
        Contact Brokers
      </h2>

      {loading ? (
        <p className="text-center text-gray-600">Loading brokers...</p>
      ) : error ? (
        <p className="text-center text-red-600">{error}</p>
      ) : brokers.length === 0 ? (
        <p className="text-center text-gray-600">No brokers found.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-4xl mx-auto">
          {brokers.map((broker, index) => (
            <div
              key={index}
              className="bg-white shadow-md rounded-xl p-6 border hover:shadow-lg transition duration-200"
            >
              <h3 className="text-xl font-semibold text-gray-800 mb-2">
                {broker.name || "Unnamed Broker"}
              </h3>
              <p className="text-gray-600 mb-1">
                ðŸ“§ Email:{" "}
                {broker.email ? (
                  <a
                    href={`mailto:${broker.email}`}
                    className="text-blue-600 underline"
                  >
                    {broker.email}
                  </a>
                ) : (
                  "N/A"
                )}
              </p>
              <p className="text-gray-600">
                ðŸ“ž Phone:{" "}
                {broker.number ? (
                  <a
                    href={`tel:${broker.phone}`}
                    className="text-blue-600 underline"
                  >
                    {broker.phone}
                  </a>
                ) : (
                  "N/A"
                )}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ContactBrokers;
