import React, { useState, useEffect } from "react";
import axios from "axios";

const ExpenseLogForm = () => {
  const [formData, setFormData] = useState({
    vehicleId: "",
    driverName: "",
    fuel: "",
    toll: "",
    food: "",
    other: "",
    date: "",
    notes: "",
  });
  const [vehicles, setVehicles] = useState([]);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:5000/api/vehicles", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setVehicles(res.data);
      } catch (err) {
        setError("Failed to fetch vehicles");
      }
    };
    fetchVehicles();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.post("http://localhost:5000/api/expense-log", formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSuccess("Expense logged successfully!");
      setFormData({
        vehicleId: "",
        driverName: "",
        fuel: "",
        toll: "",
        food: "",
        other: "",
        date: "",
        notes: "",
      });
      setError("");
    } catch (err) {
      setError(err.response?.data?.message || "Failed to log expense");
      setSuccess("");
    }
  };

  return (
    <div className="bg-white p-6 rounded-xl shadow-md">
      <h3 className="text-2xl font-semibold mb-4 text-gray-800">Expense Log</h3>
      {error && <p className="text-red-500 mb-4">{error}</p>}
      {success && <p className="text-green-500 mb-4">{success}</p>}
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-gray-700">Vehicle</label>
          <select
            name="vehicleId"
            value={formData.vehicleId}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          >
            <option value="">Select Vehicle</option>
            {vehicles.map((vehicle) => (
              <option key={vehicle._id} value={vehicle._id}>
                {vehicle.number}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label className="block text-gray-700">Driver Name</label>
          <input
            type="text"
            name="driverName"
            value={formData.driverName}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block text-gray-700">Fuel (₹)</label>
          <input
            type="number"
            name="fuel"
            value={formData.fuel}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            min="0"
            step="0.01"
          />
        </div>
        <div>
          <label className="block text-gray-700">Toll (₹)</label>
          <input
            type="number"
            name="toll"
            value={formData.toll}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            min="0"
            step="0.01"
          />
        </div>
        <div>
          <label className="block text-gray-700">Food (₹)</label>
          <input
            type="number"
            name="food"
            value={formData.food}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            min="0"
            step="0.01"
          />
        </div>
        <div>
          <label className="block text-gray-700">Other (₹)</label>
          <input
            type="number"
            name="other"
            value={formData.other}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            min="0"
            step="0.01"
          />
        </div>
        <div>
          <label className="block text-gray-700">Date</label>
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block text-gray-700">Notes</label>
          <textarea
            name="notes"
            value={formData.notes}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            rows="4"
          />
        </div>
        <button
          type="submit"
          className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
        >
          Log Expense
        </button>
      </form>
    </div>
  );
};

export default ExpenseLogForm;
