// src/components/AvailableLoads.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const AvailableLoads = () => {
  const [loads, setLoads] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchLoads = async () => {
      try {
        const token = localStorage.getItem("token"); // Assumes token is stored after login
        if (!token) {
          navigate("/login"); // Redirect to login if no token
          return;
        }

        const response = await fetch("http://localhost:5000/api/loads", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          if (response.status === 401 || response.status === 403) {
            localStorage.removeItem("token");
            navigate("/login");
            return;
          }
          throw new Error("Failed to fetch loads");
        }

        const data = await response.json();
        setLoads(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchLoads();
  }, [navigate]);

  if (loading) {
    return <div className="text-center py-10">Loading...</div>;
  }

  if (error) {
    return <div className="text-center py-10 text-red-500">Error: {error}</div>;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-red-50 py-10 px-6">
      <h2 className="text-3xl font-bold text-center text-gray-800 mb-8">
        Available Loads
      </h2>
      {loads.length === 0 ? (
        <p className="text-center text-gray-600">No loads available.</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 max-w-6xl mx-auto">
          {loads.map((load) => (
            <div
              key={load._id}
              className="bg-white border border-gray-200 shadow hover:shadow-xl rounded-2xl p-6"
            >
              <h3 className="text-lg font-semibold text-gray-700">
                {load.source} to {load.destination}
              </h3>
              <p className="text-gray-600">Load Type: {load.loadType}</p>
              <p className="text-gray-600">Weight: {load.weight}</p>
              <p className="text-gray-600">Price: â‚¹{load.price}</p>
              <p className="text-gray-600">Date: {new Date(load.date).toLocaleDateString()}</p>
              <p className="text-gray-600">
                Posted by: {load.postedBy?.email || "Unknown"} ({load.brokerNumber})
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AvailableLoads;
