import React, { useState, useEffect } from "react";
import axios from "axios";

const TripLog = () => {
  const [trips, setTrips] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTrips = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/trips");
        setTrips(response.data);
      } catch (error) {
        console.error("Error fetching trips:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchTrips();
  }, []);

  if (loading) {
    return <p className="text-center mt-4">Loading trips...</p>;
  }

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Trip Log</h2>
      {trips.length === 0 ? (
        <p>No trips found.</p>
      ) : (
        <table className="table-auto w-full border border-gray-300">
          <thead>
            <tr className="bg-gray-200">
              <th className="border px-4 py-2">Trip ID</th>
              <th className="border px-4 py-2">Vehicle</th>
              <th className="border px-4 py-2">Driver</th>
              <th className="border px-4 py-2">Start</th>
              <th className="border px-4 py-2">Destination</th>
              <th className="border px-4 py-2">Expenses</th>
            </tr>
          </thead>
          <tbody>
            {trips.map((trip, index) => (
              <tr key={trip._id || index}>
                <td className="border px-4 py-2">{trip._id}</td>
                <td className="border px-4 py-2">{trip.vehicleNumber}</td>
                <td className="border px-4 py-2">{trip.driverName}</td>
                <td className="border px-4 py-2">{trip.startLocation}</td>
                <td className="border px-4 py-2">{trip.destination}</td>
                <td className="border px-4 py-2">
                  {trip.expenses && trip.expenses.length > 0 ? (
                    <ul>
                      {trip.expenses.map((expense, expIndex) => (
                        <li key={expense._id || expIndex}>
                          {expense.type}: â‚¹{expense.amount}
                        </li>
                      ))}
                    </ul>
                  ) : (
                    "No expenses recorded"
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default TripLog;
