import React, { useState } from "react";
import axios from "axios";

const PostLoadForm = () => {
  const [load, setLoad] = useState({
  brokerEmail: "", // or get it from context/auth
  source: "",
  destination: "",
  loadType: "",
  weight: "",
  date: "",
  price: "",
});

  const handleChange = (e) => {
    setLoad({ ...load, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/post-load", load);
      alert("Load posted successfully!");
      setLoad({
        start: "",
        destination: "",
        fuel: "",
        payment: "",
        timeLimit: "",
      });
    } catch (err) {
      alert("Error posting load");
      console.error(err);
    }
  };

  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded-lg shadow-md mt-10">
      <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">
        Post a Load
      </h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="start"
          placeholder="Start Location"
          value={load.start}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 rounded px-3 py-2"
        />
        <input
          type="text"
          name="destination"
          placeholder="Destination"
          value={load.destination}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 rounded px-3 py-2"
        />
        <input
          type="number"
          name="fuel"
          placeholder="Expected Fuel (in liters)"
          value={load.fuel}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 rounded px-3 py-2"
        />
        <input
          type="number"
          name="payment"
          placeholder="Payment Offered (â‚¹)"
          value={load.payment}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 rounded px-3 py-2"
        />
        <input
          type="text"
          name="timeLimit"
          placeholder="Time Limit to Unload (e.g., 5 hrs)"
          value={load.timeLimit}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 rounded px-3 py-2"
        />
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
        >
          Post Load
        </button>
      </form>
    </div>
  );
};

export default PostLoadForm;
