import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import jsPDF from "jspdf";

const OwnerDashboard = () => {
  const navigate = useNavigate();
  const ownerName = "Welcome, Owner";
  const [vehicles, setVehicles] = useState([]);
  const [error, setError] = useState("");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    localStorage.removeItem("userType");
    navigate("/");
  };

  useEffect(() => {
    const fetchVehicles = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:5000/api/vehicles", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setVehicles(res.data);
      } catch (err) {
        console.error("Error fetching vehicles:", err);
        setError("Failed to load vehicles");
      }
    };
    fetchVehicles();
  }, []);

  const generateBill = async (vehicleId) => {
  try {
    const token = localStorage.getItem("token");

    // Step 1: Get latest completed trip for vehicle
    const tripsRes = await axios.get(`http://localhost:5000/api/trips`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    const trips = tripsRes.data.filter(
      (trip) => trip.vehicleId._id === vehicleId && trip.status === "completed"
    );

    if (trips.length === 0) {
      alert("No completed trips found for this vehicle.");
      return;
    }

    const latestTrip = trips[0]; // assuming trips are sorted by date desc

    // Step 2: Get bill for that trip
    const billRes = await axios.get(`http://localhost:5000/api/bill/${latestTrip._id}`, {
      headers: { Authorization: `Bearer ${token}` },
    });

    const { trip, expenses, loads, totalAmount } = billRes.data;

    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text(`Bill for Vehicle: ${trip.vehicleId.number}`, 20, 20);
    doc.setFontSize(12);

    let y = 40;
    loads.forEach((load, i) => {
      doc.text(`${i + 1}. Load from ${load.source} to ${load.destination} - Price: â‚¹${load.price}`, 20, y);
      y += 10;
    });

    expenses.forEach((exp, i) => {
      doc.text(`${i + 1}. Expense: ${exp.expenseName} - â‚¹${exp.amount}`, 20, y);
      y += 10;
    });

    doc.text(`Total Amount: â‚¹${totalAmount}`, 20, y + 10);
    doc.save(`bill_${vehicleId}.pdf`);
  } catch (err) {
    console.error("Error generating bill:", err);
    alert("Failed to generate bill.");
  }
};

  const actions = [
    { label: "ðŸ“‹ View Available Loads", path: "/loads" },
    { label: "ðŸš› Register Vehicle", path: "/add-vehicle" },
    { label: "ðŸ‘· Vehicle Status", path: "/vehicle-status" },
    { label: "ðŸ“ž Contact Brokers", path: "/contact-brokers" },
  ];

  const availableVehicles = vehicles.filter(v => v.status === "available" || v.status === "transit");
  const unavailableVehicles = vehicles.filter(v => v.status !== "available" && v.status !== "transit");

  return (
    <div className="min-h-screen bg-light-gray py-10 px-6">
      <div className="flex justify-between items-center max-w-6xl mx-auto mb-6">
        <div>
          <h2 className="text-4xl font-bold text-dark-blue mb-2">
            LorryTracker Owner Dashboard
          </h2>
          <p className="text-lg text-cool-gray">{ownerName}</p>
        </div>
        <button
          onClick={handleLogout}
          className="bg-red-600 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded-lg transition duration-200"
        >
          Logout
        </button>
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 gap-6 max-w-4xl mx-auto mb-10">
        {actions.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className="bg-white border border-cool-gray shadow hover:shadow-xl rounded-2xl p-6 text-center text-lg font-semibold text-dark-blue hover:bg-cool-gray hover:text-light-gray transition duration-200"
          >
            {item.label}
          </Link>
        ))}
      </div>

      {/* Vehicle Availability Section */}
      <div className="max-w-6xl mx-auto">
        <h3 className="text-2xl font-bold text-dark-blue mb-6">Vehicle Availability</h3>

        {error && <p className="text-red-600">{error}</p>}

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Available / Transit Vehicles */}
          <div>
            <h4 className="text-xl font-semibold text-dark-blue mb-4 flex items-center">
              <span className="w-3 h-3 bg-green-500 rounded-full mr-2"></span>
              Active Vehicles ({availableVehicles.length})
            </h4>
            <div className="space-y-4">
              {availableVehicles.map((vehicle) => (
                <div key={vehicle._id} className="bg-white border border-cool-gray rounded-lg p-4 shadow-sm">
                  <div className="flex justify-between items-start">
                    <div>
                      <h5 className="font-semibold text-dark-blue">{vehicle.number}</h5>
                      <p className="text-sm text-cool-gray">{vehicle.type} â€¢ {vehicle.capacity} tons</p>
                      <p className="text-sm text-cool-gray">Driver: {vehicle.driver || "N/A"}</p>
                    </div>
                    <div className="flex flex-col items-end">
                      <span className={`text-xs font-medium px-2.5 py-0.5 rounded-full ${
                        vehicle.status === "Available"||"available" ? "bg-green-100 text-green-800" : "bg-yellow-100 text-yellow-800"
                      }`}>
                        {vehicle.status}
                      </span>
                      <button
                        onClick={() => generateBill(vehicle._id)}
                        className="mt-2 bg-blue-500 text-white text-xs px-2 py-1 rounded hover:bg-blue-600"
                      >
                        Generate Bill
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Unavailable Vehicles */}
          <div>
            <h4 className="text-xl font-semibold text-dark-blue mb-4 flex items-center">
              <span className="w-3 h-3 bg-red-500 rounded-full mr-2"></span>
              Unavailable Vehicles ({unavailableVehicles.length})
            </h4>
            <div className="space-y-4">
              {unavailableVehicles.map((vehicle) => (
                <div key={vehicle._id} className="bg-white border border-cool-gray rounded-lg p-4 shadow-sm">
                  <div className="flex justify-between items-start">
                    <div>
                      <h5 className="font-semibold text-dark-blue">{vehicle.number}</h5>
                      <p className="text-sm text-cool-gray">{vehicle.type} â€¢ {vehicle.capacity} tons</p>
                      <p className="text-sm text-cool-gray">Driver: {vehicle.driver || "N/A"}</p>
                    </div>
                    <span className="bg-red-100 text-red-800 text-xs font-medium px-2.5 py-0.5 rounded-full">
                      {vehicle.status}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OwnerDashboard;
