// src/components/ContactBrokers.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const ContactBrokers = () => {
  const [brokers, setBrokers] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/brokers")
      .then(res => setBrokers(res.data))
      .catch(err => console.error("Error fetching brokers:", err));
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-red-50 p-8">
      <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">
        Contact Brokers
      </h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-4xl mx-auto">
        {brokers.map((broker, index) => (
          <div
            key={index}
            className="bg-white shadow-md rounded-xl p-6 border hover:shadow-lg transition duration-200"
          >
            <h3 className="text-xl font-semibold text-gray-800">{broker.email}</h3>
            <p className="text-gray-600">Role: {broker.role}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ContactBrokers;
