import React, { useEffect, useState } from "react";
import axios from "axios";

const TripLog = () => {
  const [loads, setLoads] = useState([]);
  const [checklists, setChecklists] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const loadsRes = await axios.get("http://localhost:5000/api/loads");
      const checklistsRes = await axios.get("http://localhost:5000/api/checklists");
      const expensesRes = await axios.get("http://localhost:5000/api/expenses");

      setLoads(loadsRes.data);
      setChecklists(checklistsRes.data);
      setExpenses(expensesRes.data);
    } catch (err) {
      setError("Failed to load data.");
    }
  };

  const deleteChecklist = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/checklists/${id}`);
      setChecklists((prev) => prev.filter((c) => c._id !== id));
    } catch (err) {
      console.error("Failed to delete checklist:", err);
    }
  };

  const deleteExpense = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/expenses/${id}`);
      setExpenses((prev) => prev.filter((e) => e._id !== id));
    } catch (err) {
      console.error("Failed to delete expense:", err);
    }
  };

  return (
    <div className="max-w-6xl mx-auto p-6">
      <h1 className="text-2xl font-bold mb-4">Trip Log</h1>
      {error && <p className="text-red-500">{error}</p>}

      {/* Loads Section */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-3">Loads</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {loads.map((load, index) => (
            <div
              key={load._id || `load-${index}`}
              className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow"
            >
              <p><strong>Load ID:</strong> {load._id}</p>
              <p><strong>From:</strong> {load.start}</p>
              <p><strong>To:</strong> {load.destination}</p>
              {/* Status Badge */}
              <span
                className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                  load.status === "available"
                    ? "bg-green-100 text-green-800"
                    : load.status === "booked"
                    ? "bg-yellow-100 text-yellow-800"
                    : "bg-gray-100 text-gray-800"
                }`}
              >
                {load.status || "unknown"}
              </span>
            </div>
          ))}
        </div>
      </div>

      {/* Checklists Section */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-3">Checklists</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {checklists.map((checklist, index) => (
            <div
              key={checklist._id || `checklist-${index}`}
              className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow"
            >
              <p><strong>Vehicle:</strong> {checklist.vehicle}</p>
              <p><strong>Driver:</strong> {checklist.driver}</p>
              <p><strong>Status:</strong> {checklist.status}</p>
              <button
                onClick={() => deleteChecklist(checklist._id)}
                className="mt-3 px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Expenses Section */}
      <div>
        <h2 className="text-xl font-semibold mb-3">Expenses</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {expenses.map((expense, index) => (
            <div
              key={expense._id || `expense-${index}`}
              className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow"
            >
              <p><strong>Category:</strong> {expense.category}</p>
              <p><strong>Amount:</strong> â‚¹{expense.amount}</p>
              <button
                onClick={() => deleteExpense(expense._id)}
                className="mt-3 px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TripLog;
