// src/pages/LoginPage.jsx
import React from "react";
import { useNavigate } from "react-router-dom";

const LoginPage = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-8">
      <h1 className="text-4xl font-bold mb-4 text-red-700">Truck Tracker</h1>

      <div className="max-w-2xl text-center mb-6">
        <h2 className="text-2xl font-semibold mb-2">About</h2>
        <p className="text-gray-700">
          Welcome to <strong>Truck Tracker</strong> â€“ a logistics management platform for lorry owners, drivers, and brokers.
          Manage vehicles, assign trips, log expenses, and auto-generate invoices with ease.
        </p>
      </div>

      <div className="flex flex-wrap gap-4 mt-4 justify-center">
        <button
          className="bg-red-700 text-white px-6 py-2 rounded hover:bg-red-800"
          onClick={() => navigate("/owner-dashboard")}
        >
          Login as Owner
        </button>
        <button
          className="bg-blue-700 text-white px-6 py-2 rounded hover:bg-blue-800"
          onClick={() => navigate("/driver-dashboard")}
        >
          Login as Driver
        </button>
        <button
          className="bg-green-700 text-white px-6 py-2 rounded hover:bg-green-800"
          onClick={() => navigate("/broker-dashboard")}
        >
          Login as Broker
        </button>
        <button
          className="bg-gray-800 text-white px-6 py-2 rounded hover:bg-black"
          onClick={() => navigate("/signup")}
        >
          Signup (Owner/Broker/Driver)
        </button>
      </div>
    </div>
  );
};

export default LoginPage;
