import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const API_URL = "http://localhost:5000/api";

const EditLoad = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    brokerName: "",
    brokerNumber: "",
    brokerAddress: "",
    loadType: "",
    weight: "",
    pickupLocation: "",
    dropLocation: "",
    rate: ""
  });

  const [loading, setLoading] = useState(true);

  // Fetch load details on mount
  useEffect(() => {
    const fetchLoad = async () => {
      try {
        const res = await axios.get(`${API_URL}/loads/${id}`);
        setFormData({
          brokerName: res.data.brokerName || "",
          brokerNumber: res.data.brokerNumber || "",
          brokerAddress: res.data.brokerAddress || "",
          loadType: res.data.loadType || "",
          weight: res.data.weight || "",
          pickupLocation: res.data.pickupLocation || "",
          dropLocation: res.data.dropLocation || "",
          rate: res.data.rate || ""
        });
        setLoading(false);
      } catch (err) {
        console.error("Error fetching load:", err);
        setLoading(false);
      }
    };

    fetchLoad();
  }, [id]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`${API_URL}/loads/${id}`, formData);
      alert("Load updated successfully!");
      navigate("/loads");
    } catch (err) {
      console.error("Error updating load:", err);
    }
  };

  if (loading) return <p>Loading load details...</p>;

  return (
    <div className="p-6 max-w-xl mx-auto bg-white shadow-md rounded-lg">
      <h2 className="text-2xl font-bold mb-4">Edit Load</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        
        <input
          type="text"
          name="brokerName"
          value={formData.brokerName}
          onChange={handleChange}
          placeholder="Broker Name"
          className="border p-2 w-full rounded"
        />

        <input
          type="text"
          name="brokerNumber"
          value={formData.brokerNumber}
          onChange={handleChange}
          placeholder="Broker Number"
          className="border p-2 w-full rounded"
        />

        <input
          type="text"
          name="brokerAddress"
          value={formData.brokerAddress}
          onChange={handleChange}
          placeholder="Broker Address"
          className="border p-2 w-full rounded"
        />

        <input
          type="text"
          name="loadType"
          value={formData.loadType}
          onChange={handleChange}
          placeholder="Load Type"
          className="border p-2 w-full rounded"
        />

        <input
          type="number"
          name="weight"
          value={formData.weight}
          onChange={handleChange}
          placeholder="Weight"
          className="border p-2 w-full rounded"
        />

        <input
          type="text"
          name="pickupLocation"
          value={formData.pickupLocation}
          onChange={handleChange}
          placeholder="Pickup Location"
          className="border p-2 w-full rounded"
        />

        <input
          type="text"
          name="dropLocation"
          value={formData.dropLocation}
          onChange={handleChange}
          placeholder="Drop Location"
          className="border p-2 w-full rounded"
        />

        <input
          type="number"
          name="rate"
          value={formData.rate}
          onChange={handleChange}
          placeholder="Rate"
          className="border p-2 w-full rounded"
        />

        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Update Load
        </button>
      </form>
    </div>
  );
};

export default EditLoad;
