import React, { useState } from "react";

const ExpenseLogger = () => {
  const [expense, setExpense] = useState({
    vehicleNumber: "",
    driverName: "",
    tripDate: "",
    fuel: "",
    tolls: "",
    food: "",
    other: "",
  });

  const handleChange = (e) => {
    setExpense({ ...expense, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Logged Expense:", expense);
    // Submit expense data to backend
  };

  return (
    <div className="max-w-xl mx-auto mt-10 bg-white shadow-md p-8 rounded-lg">
      <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">
        Log Trip Expenses
      </h2>
      <form onSubmit={handleSubmit} className="space-y-5">
        <input
          type="text"
          name="vehicleNumber"
          placeholder="Vehicle Number"
          value={expense.vehicleNumber}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
          required
        />
        <input
          type="text"
          name="driverName"
          placeholder="Driver Name"
          value={expense.driverName}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
        />
        <input
          type="date"
          name="tripDate"
          value={expense.tripDate}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
          required
        />
        <input
          type="number"
          name="fuel"
          placeholder="Fuel Expense (₹)"
          value={expense.fuel}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
        />
        <input
          type="number"
          name="tolls"
          placeholder="Toll Expense (₹)"
          value={expense.tolls}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
        />
        <input
          type="number"
          name="food"
          placeholder="Food Expense (₹)"
          value={expense.food}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
        />
        <input
          type="number"
          name="others"
          placeholder="Miscellaneous (₹)"
          value={expense.miscellaneous}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
        />
        <button
          type="submit"
          className="bg-blue-600 hover:bg-blue-700 text-white w-full py-3 rounded-md font-semibold"
        >
          Log Expense
        </button>
      </form>
    </div>
  );
};

export default ExpenseLogger;
