import React, { useState, useEffect } from "react";
import axios from "axios";

const API_URL = "http://localhost:5000/api/expense-log"; // Update if needed

const Expense = () => {
  const [form, setForm] = useState({
    vehicleNumber: "",
    driverName: "",
    tripDate: "",
    amount: "",
    expenseName: "",
    category: "",
    fuel: 0,
    tolls: 0,
    food: 0,
    other: 0,
    notes: "",
  });
  const [expenses, setExpenses] = useState([]);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchExpenses();
  }, []);

  const fetchExpenses = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(API_URL, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setExpenses(res.data || []);
      setError("");
    } catch (err) {
      setError("Failed to fetch expenses");
    }
  };

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === "number" ? Number(value) : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccess("");
    try {
      const token = localStorage.getItem("token");
      const payload = {
        vehicleNumber: form.vehicleNumber,
        driverName: form.driverName,
        tripDate: form.tripDate,
        amount: form.amount,
        expenseName: form.expenseName,
        category: form.category,
        fuel: form.fuel,
        tolls: form.tolls,
        food: form.food,
        other: form.other,
        notes: form.notes,
      };
      await axios.post(API_URL, payload, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSuccess("Expense logged successfully!");
      setForm({
        vehicleNumber: "",
        driverName: "",
        tripDate: "",
        amount: "",
        expenseName: "",
        category: "",
        fuel: 0,
        tolls: 0,
        food: 0,
        other: 0,
        notes: "",
      });
      fetchExpenses();
    } catch (err) {
      setError(err.response?.data?.message || "Failed to log expense");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white rounded-xl shadow-md">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Expense Log</h2>

      {error && (
        <div className="bg-red-50 border border-red-400 text-red-700 p-3 mb-6 rounded">
          {error}
        </div>
      )}
      {success && (
        <div className="bg-green-50 border border-green-400 text-green-700 p-3 mb-6 rounded">
          {success}
        </div>
      )}

      <form onSubmit={handleSubmit} className="grid grid-cols-1 gap-4 mb-8">
        <input
          type="text"
          name="vehicleNumber"
          placeholder="Vehicle Number"
          value={form.vehicleNumber}
          onChange={handleChange}
          required
          className="p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        <input
          type="text"
          name="driverName"
          placeholder="Driver Name"
          value={form.driverName}
          onChange={handleChange}
          required
          className="p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        <input
          type="date"
          name="tripDate"
          placeholder="Trip Date"
          value={form.tripDate}
          onChange={handleChange}
          className="p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        <input
          type="number"
          name="amount"
          placeholder="Total Amount"
          value={form.amount}
          onChange={handleChange}
          min="0"
          step="0.01"
          className="p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
          required
        />
        <input
          type="text"
          name="expenseName"
          placeholder="Expense Name"
          value={form.expenseName}
          onChange={handleChange}
          className="p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        <input
          type="text"
          name="category"
          placeholder="Category"
          value={form.category}
          onChange={handleChange}
          className="p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        <input
          type="number"
          name="fuel"
          placeholder="Fuel ₹"
          value={form.fuel}
          onChange={handleChange}
          min="0"
          step="0.01"
          className="p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        <input
          type="number"
          name="tolls"
          placeholder="Tolls ₹"
          value={form.tolls}
          onChange={handleChange}
          min="0"
          step="0.01"
          className="p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        <input
          type="number"
          name="food"
          placeholder="Food ₹"
          value={form.food}
          onChange={handleChange}
          min="0"
          step="0.01"
          className="p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        <input
          type="number"
          name="other"
          placeholder="Other ₹"
          value={form.other}
          onChange={handleChange}
          min="0"
          step="0.01"
          className="p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        <textarea
          name="notes"
          placeholder="Additional notes"
          value={form.notes}
          onChange={handleChange}
          rows={3}
          className="p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
        />

        <button
          type="submit"
          disabled={loading}
          className="bg-green-600 text-white py-3 rounded-md hover:bg-green-700 transition disabled:opacity-50"
        >
          {loading ? "Logging Expense..." : "Log Expense"}
        </button>
      </form>

      <hr className="my-6" />

      <h3 className="text-2xl font-semibold mb-4 text-gray-700">Logged Expenses</h3>
      {expenses.length === 0 ? (
        <p className="text-gray-500">No expenses logged yet.</p>
      ) : (
        <div className="space-y-4">
          {expenses.map((exp) => (
            <div
              key={exp._id}
              className="border border-gray-300 rounded-md p-4 shadow-sm hover:shadow-md transition"
            >
              <p><strong>Vehicle Number:</strong> {exp.vehicleNumber || "N/A"}</p>
              <p><strong>Driver Name:</strong> {exp.driverName || "N/A"}</p>
              <p><strong>Trip Date:</strong> {exp.tripDate ? new Date(exp.tripDate).toLocaleDateString() : "N/A"}</p>
              <p><strong>Total Amount:</strong> ₹{exp.amount ?? (exp.fuel + exp.tolls + exp.food + exp.other)}</p>
              <p><strong>Expense Name:</strong> {exp.expenseName || "N/A"}</p>
              <p><strong>Category:</strong> {exp.category || "N/A"}</p>
              <p><strong>Fuel:</strong> ₹{exp.fuel ?? 0}</p>
              <p><strong>Tolls:</strong> ₹{exp.tolls ?? 0}</p>
              <p><strong>Food:</strong> ₹{exp.food ?? 0}</p>
              <p><strong>Other:</strong> ₹{exp.other ?? 0}</p>
              {exp.notes && <p><strong>Notes:</strong> {exp.notes}</p>}
              <p className="text-xs text-gray-500 mt-2">
                Created: {exp.createdAt ? new Date(exp.createdAt).toLocaleString() : "N/A"}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Expense;
