import React, { useState } from "react";

const PostLoadForm = () => {
  const [load, setLoad] = useState({
    start: "",
    destination: "",
    fuel: "",
    payment: "",
    timeLimit: "",
  });

  const handleChange = (e) => {
    setLoad({ ...load, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // TODO: Replace with actual POST API request
    console.log("Posted Load:", load);

    // Reset form
    setLoad({
      start: "",
      destination: "",
      fuel: "",
      payment: "",
      timeLimit: "",
    });
  };

  return (
    <div className="max-w-xl mx-auto bg-white p-6 rounded-lg shadow-md mt-10">
      <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">
        Post a Load
      </h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="start"
          placeholder="Start Location"
          value={load.start}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 rounded px-3 py-2"
        />
        <input
          type="text"
          name="destination"
          placeholder="Destination"
          value={load.destination}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 rounded px-3 py-2"
        />
        <input
          type="number"
          name="fuel"
          placeholder="Expected Fuel (in liters)"
          value={load.fuel}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 rounded px-3 py-2"
        />
        <input
          type="number"
          name="payment"
          placeholder="Payment Offered (â‚¹)"
          value={load.payment}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 rounded px-3 py-2"
        />
        <input
          type="text"
          name="timeLimit"
          placeholder="Time Limit to Unload (e.g., 5 hrs)"
          value={load.timeLimit}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 rounded px-3 py-2"
        />
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition"
        >
          Post Load
        </button>
      </form>
    </div>
  );
};

export default PostLoadForm;
