import React, { useState } from "react";
import {
  Link,
  Routes,
  Route,
  Navigate,
  useLocation,
  useNavigate,
} from "react-router-dom";
import TripChecklistForm from "./TripChecklistForm";
import ExpenseLogForm from "./ExpenseLogForm";
import TripLog from "./TripLog";

const DriverDashboard = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [isNavigating, setIsNavigating] = useState(false);

  console.log("Current path:", location.pathname); // Debug: Log current path

  const handleNavigation = (path) => {
    if (isNavigating || location.pathname === `/driver-dashboard/${path}`) {
      console.log(
        `Navigation to ${path} blocked: already navigating or on same path`
      );
      return;
    }
    setIsNavigating(true);
    navigate(path, { replace: true });
    console.log(`Navigating to: /driver-dashboard/${path}`);
    setTimeout(() => setIsNavigating(false), 500); // Debounce for 500ms
  };

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-6">
      <h2 className="text-3xl font-bold text-center mb-8 text-gray-800">
        Driver Dashboard
      </h2>
      <p className="text-center text-gray-600 mb-4">
        Current Path: {location.pathname}
      </p>
      <nav className="mb-8">
        <ul className="flex justify-center space-x-4">
          {[
            { label: "Trip Checklist", path: "driver-trips" },
            { label: "Trip Log", path: "trip-log" },
            { label: "Expenses Log", path: "driver-expenses" },
          ].map((item) => {
            const isActive =
              location.pathname === `/driver-dashboard/${item.path}`;
            return (
              <li key={item.path}>
                <button
                  onClick={() => handleNavigation(item.path)}
                  disabled={isActive || isNavigating}
                  className={`px-4 py-2 rounded-md text-lg font-semibold transition ${
                    isActive || isNavigating
                      ? "bg-green-500 text-white cursor-not-allowed"
                      : "bg-white text-gray-700 hover:bg-green-50"
                  }`}
                >
                  {item.label}
                </button>
              </li>
            );
          })}
        </ul>
      </nav>
      <div className="max-w-4xl mx-auto">
        <Routes>
          <Route path="/" element={<Navigate to="driver-trips" replace />} />
          <Route path="driver-trips" element={<TripChecklistForm />} />
          <Route path="driver-expenses" element={<ExpenseLogForm />} />
          <Route path="trip-log" element={<TripLog />} />
          <Route
            path="*"
            element={
              <div className="text-center text-red-500">
                <p>Invalid route: {location.pathname}</p>
                <p>Redirecting to Trip Checklist...</p>
                <Navigate to="driver-trips" replace />
              </div>
            }
          />
        </Routes>
      </div>
    </div>
  );
};

export default DriverDashboard;
