// src/App.jsx
import React from "react";
import "./index.css";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

// Pages & Components
import LoginPage from "./pages/LoginPage";
import SignupForm from "./pages/SignupForm"; // <-- Add this line
import OwnerDashboard from "./components/OwnerDashboard";
import DriverDashboard from "./components/DriverDashboard";
import BrokerDashboard from "./components/BrokerDashboard";
import AssignDriverForm from "./components/AssignDriverForm";
import ExpenseLogger from "./components/ExpenseLogger";
import InvoiceGenerator from "./components/InvoiceGenerator";
import TripPlanner from "./components/TripPlanner";
import VehicleForm from "./components/VehicleForm";
import ContactBrokers from "./components/ContactBrokers";
// import Navbar from "./components/Navbar"; // Optional if you want to show navbar globally

const App = () => {
  return (
    <Router>
      {/* <Navbar /> */}
      <Routes>
        <Route path="/" element={<LoginPage />} />
        <Route path="/signup" element={<SignupForm />} /> {/* <-- Signup route added */}
        <Route path="/owner-dashboard" element={<OwnerDashboard />} />
        <Route path="/driver-dashboard" element={<DriverDashboard />} />
        <Route path="/broker-dashboard" element={<BrokerDashboard />} />
        <Route path="/assign-driver" element={<AssignDriverForm />} />
        <Route path="/log-expense" element={<ExpenseLogger />} />
        <Route path="/generate-invoice" element={<InvoiceGenerator />} />
        <Route path="/plan-trip" element={<TripPlanner />} />
        <Route path="/add-vehicle" element={<VehicleForm />} />
        <Route path="/contact-brokers" element={<ContactBrokers/>} />
<Route path="/post-load" element={<PostLoadForm />} />
      </Routes>
    </Router>
  );
};

export default App;
