  import React, { useState } from "react";
  import axios from "axios";

  const ExpenseLogger = () => {
    const [expense, setExpense] = useState({
      vehicleNumber: "",
      driverName: "",
      tripDate: "",
      fuel: "",
      tolls: "",
      food: "",
      other: "",
    });

    const [message, setMessage] = useState("");

    const handleChange = (e) => {
      setExpense({ ...expense, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
      e.preventDefault();

      // Calculate amount
      const amount =
        (parseFloat(expense.fuel) || 0) +
        (parseFloat(expense.tolls) || 0) +
        (parseFloat(expense.food) || 0) +
        (parseFloat(expense.other) || 0);

      try {
        const token = localStorage.getItem("token");
        await axios.post(
          "http://localhost:5000/api/expense-log",
          { ...expense, amount },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setMessage("Expense logged successfully!");
        setExpense({
          vehicleNumber: "",
          driverName: "",
          tripDate: "",
          fuel: "",
          tolls: "",
          food: "",
          other: "",
        });
      } catch (err) {
        setMessage(err.response?.data?.message || "Failed to log expense");
      }
    };

    return (
      <div className="max-w-xl mx-auto mt-10 bg-white shadow-md p-8 rounded-lg">
        <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">
          Log Trip Expenses
        </h2>
        {message && (
          <p
            className={`mb-4 ${
              message.includes("success") ? "text-green-500" : "text-red-500"
            }`}
          >
            {message}
          </p>
        )}
        <form onSubmit={handleSubmit} className="space-y-5">
          <input
            type="text"
            name="vehicleNumber"
            placeholder="Vehicle Number"
            value={expense.vehicleNumber}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-md p-3"
            required
          />
          <input
            type="text"
            name="driverName"
            placeholder="Driver Name"
            value={expense.driverName}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-md p-3"
          />
          <input
            type="date"
            name="tripDate"
            value={expense.tripDate}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-md p-3"
            required
          />
          <input
            type="number"
            name="fuel"
            placeholder="Fuel Expense (₹)"
            value={expense.fuel}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-md p-3"
          />
          <input
            type="number"
            name="tolls"
            placeholder="Toll Expense (₹)"
            value={expense.tolls}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-md p-3"
          />
          <input
            type="number"
            name="food"
            placeholder="Food Expense (₹)"
            value={expense.food}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-md p-3"
          />
          <input
            type="number"
            name="other"
            placeholder="Other (₹)"
            value={expense.other}
            onChange={handleChange}
            className="w-full border border-gray-300 rounded-md p-3"
          />
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-700 text-white w-full py-3 rounded-md font-semibold"
          >
            Log Expense
          </button>
        </form>
      </div>
    );
  };

  export default ExpenseLogger;
