import React, { useState, useEffect } from "react";
import axios from "axios";

const TripLog = () => {
  const [checklists, setChecklists] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");
        const [checklistRes, expenseRes] = await Promise.all([
          axios.get("http://localhost:5000/api/trip-checklist", {
            headers: { Authorization: `Bearer ${token}` },
          }),
          axios.get("http://localhost:5000/api/expense-log", {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);
        setChecklists(checklistRes.data);
        setExpenses(expenseRes.data);
        setError("");
      } catch (err) {
        setError(err.response?.data?.message || "Failed to fetch trip log");
      }
    };
    fetchData();
  }, []);

  return (
    <div className="bg-white p-6 rounded-xl shadow-md">
      <h3 className="text-2xl font-semibold mb-4 text-gray-800">Trip Log</h3>
      {error && <p className="text-red-500 mb-4">{error}</p>}

      <div className="mb-8">
        <h4 className="text-xl font-semibold mb-2">Trip Checklists</h4>
        {checklists.length === 0 ? (
          <p className="text-gray-600">No checklists found.</p>
        ) : (
          <div className="space-y-4">
            {checklists.map((checklist, index) => (
  <div key={checklist._id || `checklist-${index}`} className="border p-4 rounded">
    <p><strong>Load Weight:</strong> {checklist.loadWeight} tonnes</p>
    <p><strong>Load Type:</strong> {checklist.loadType}</p>
    <p><strong>Source:</strong> {checklist.source}</p>
    <p><strong>Destination:</strong> {checklist.destination}</p>
    <p><strong>Vehicle Number:</strong> {checklist.vehicleNumber}</p>
    <p><strong>Notes:</strong> {checklist.notes || "N/A"}</p>
    <p><strong>Created:</strong> {new Date(checklist.createdAt).toLocaleString()}</p>
  </div>
))}

          </div>
        )}
      </div>

      <div>
        <h4 className="text-xl font-semibold mb-2">Expenses</h4>
        {expenses.length === 0 ? (
          <p className="text-gray-600">No expenses found.</p>
        ) : (
          <div className="space-y-4">
            {expenses.map((expense, index) => (
  <div key={expense._id || `expense-${index}`} className="border p-4 rounded">
    <p><strong>Amount:</strong> ${expense.amount}</p>
    <p><strong>Expense Name:</strong> {expense.expenseName}</p>
    <p><strong>Category:</strong> {expense.category}</p>
    <p><strong>Date:</strong> {new Date(expense.date).toLocaleDateString()}</p>
    <p><strong>Notes:</strong> {expense.notes || "N/A"}</p>
    <p><strong>Created:</strong> {new Date(expense.createdAt).toLocaleString()}</p>
  </div>
))}

          </div>
        )}
      </div>
    </div>
  );
};

export default TripLog;
