// components/VehicleStatusForm.jsx

import React, { useState } from "react";
import axios from "axios";

const VehicleStatus = () => {
  const [vehicleNumber, setVehicleNumber] = useState("");
  const [status, setStatus] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/vehicles/update-status", {
        vehicleNumber,
        status,
      });
      setMessage("Status updated successfully.");
      setVehicleNumber("");
      setStatus("");
    } catch (error) {
      setMessage("Error updating status.");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 border rounded shadow">
      <h2 className="text-2xl font-semibold mb-4 text-center">Update Vehicle Status</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block mb-1">Vehicle Number</label>
          <input
            type="text"
            value={vehicleNumber}
            onChange={(e) => setVehicleNumber(e.target.value)}
            required
            className="w-full px-3 py-2 border rounded"
            placeholder="Enter vehicle number"
          />
        </div>
        <div>
          <label className="block mb-1">Status</label>
          <select
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            required
            className="w-full px-3 py-2 border rounded"
          >
            <option value="">Select status</option>
            <option value="Available">Available</option>
            <option value="On Trip">On Trip</option>
            <option value="Under Maintenance">Under Maintenance</option>
          </select>
        </div>
        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
        >
          Update Status
        </button>
        {message && <p className="text-center mt-2">{message}</p>}
      </form>
    </div>
  );
};

export default VehicleStatus;
