import React from "react";
import { Link } from "react-router-dom";

const Dashboard = () => {
  const ownerName = "Welcome, Owner"; // Replace with dynamic value if needed

  const actions = [
    { label: "ðŸ“‹ View Available Loads", path: "/available-loads" },
    { label: "ðŸšš Add Vehicle", path: "/add-vehicle" },
    { label: "ðŸ‘· Assign Driver", path: "/assign-driver" },
    { label: "ðŸ§­ Plan Trip", path: "/trip-planner" },
    { label: "ðŸ§¾ Log Expenses", path: "/log-expense" },
    { label: "ðŸ“„ Generate Invoice", path: "/invoice" },
    { label: "ðŸ’° Earnings Overview", path: "/earnings" },
    { label: "ðŸ“ž Contact Brokers", path: "/brokers" },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-red-50 py-10 px-6">
      <h2 className="text-4xl font-bold text-center text-gray-800 mb-2">
        LorryTracker Owner Dashboard
      </h2>
      <p className="text-center text-lg text-gray-600 mb-10">{ownerName}</p>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 max-w-6xl mx-auto">
        {actions.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className="bg-white border border-gray-200 shadow hover:shadow-xl rounded-2xl p-6 text-center text-lg font-semibold text-gray-700 hover:bg-red-100 transition duration-200"
          >
            {item.label}
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
