// App.jsx

import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import NotFound from "./pages/NotFound";

// Forms and Features
import VehicleForm from "./components/VehicleForm";
import AssignDriverForm from "./components/AssignDriverForm";
import TripPlanner from "./components/TripPlanner";
import ExpenseLogger from "./components/ExpenseLogger";
import InvoiceGenerator from "./components/InvoiceGenerator";
import Dashboard from "./components/Dashboard";
import VehicleStatusForm from "./components/VehicleStatusForm"; // ✅ Import here

function App() {
  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/vehicle-form" element={<VehicleForm />} />
        <Route path="/assign-driver" element={<AssignDriverForm />} />
        <Route path="/trip-planner" element={<TripPlanner />} />
        <Route path="/expense-logger" element={<ExpenseLogger />} />
        <Route path="/invoice-generator" element={<InvoiceGenerator />} />
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/vehicle-status" element={<VehicleStatusForm />} /> {/* ✅ Route added */}
        <Route path="*" element={<NotFound />} />
      </Routes>
    </Router>
  );
}

export default App;
