import React, { useState } from "react";

const AssignDriverForm = () => {
  const [assignData, setAssignData] = useState({
    vehicleNumber: "",
    driverName: "",
    contact: "",
    licenseNumber: "",
  });

  const handleChange = (e) => {
    setAssignData({ ...assignData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Driver Assigned:", assignData);
    // Send assignData to backend here
  };

  return (
    <div className="max-w-xl mx-auto mt-10 bg-white shadow-md p-8 rounded-lg">
      <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">
        Assign Driver to Vehicle
      </h2>
      <form onSubmit={handleSubmit} className="space-y-5">
        <input
          type="text"
          name="vehicleNumber"
          placeholder="Vehicle Number"
          value={assignData.vehicleNumber}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
          required
        />
        <input
          type="text"
          name="driverName"
          placeholder="Driver Name"
          value={assignData.driverName}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
          required
        />
        <input
          type="text"
          name="contact"
          placeholder="Driver Contact Number"
          value={assignData.contact}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
        />
        <input
          type="text"
          name="licenseNumber"
          placeholder="Driver License Number"
          value={assignData.licenseNumber}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
        />
        <button
          type="submit"
          className="bg-red-600 hover:bg-red-700 text-white w-full py-3 rounded-md font-semibold"
        >
          Assign Driver
        </button>
      </form>
    </div>
  );
};

export default AssignDriverForm;
