import React from "react";
import { Link, Routes, Route } from "react-router-dom";
import TripChecklistForm from "./TripChecklistForm";
import ExpenseLogForm from "./ExpenseLogForm";
import TripLog from "./TripLog";

const DriverDashboard = () => {
  return (
    <div className="min-h-screen bg-gray-100 py-10 px-6">
      <h2 className="text-3xl font-bold text-center mb-8 text-gray-800">
        Driver Dashboard
      </h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 max-w-6xl mx-auto">
        {[
          { label: "Trip Checklist", path: "/TripChecklistForm" },
          { label: "Trip Log", path: "/ExpenseLogForm" },
          { label: "Expenses Log", path: "/driver-expenses" },
        ].map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className="bg-white shadow-md hover:shadow-xl rounded-xl p-6 text-center text-lg font-semibold text-gray-700 hover:bg-green-50 transition"
          >
            {item.label}
          </Link>
        ))}
      </div>
      <div className="max-w-4xl mx-auto mt-8">
        <Routes>
          <Route path="/driver-trips" element={<TripChecklistForm />} />
          <Route path="/driver-expenses" element={<ExpenseLogForm />} />
          <Route path="/trip-log" element={<TripLog />} />
        </Routes>
      </div>
    </div>
  );
};

export default DriverDashboard;
