import React, { useState, useEffect } from "react";
import axios from "axios";

const PostLoadForm = () => {
  const [load, setLoad] = useState({
    brokerEmail: "",
    source: "",
    destination: "",
    loadType: "",
    weight: "",
    date: "",
    price: "",
  });

  // Auto-fill brokerEmail from localStorage
  useEffect(() => {
    const email = localStorage.getItem("email");
    if (email) {
      setLoad((prev) => ({ ...prev, brokerEmail: email }));
    }
  }, []);

  const handleChange = (e) => {
    setLoad({ ...load, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:5000/api/post-load", load);
      alert("Load posted successfully");
      setLoad({
        brokerEmail: load.brokerEmail, // preserve email
        source: "",
        destination: "",
        loadType: "",
        weight: "",
        date: "",
        price: "",
      });
    } catch (err) {
      console.error(err);
      alert("Failed to post load");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 max-w-md mx-auto">
      {/* Auto-filled, read-only email */}
      <input
        type="email"
        name="brokerEmail"
        value={load.brokerEmail}
        disabled
        className="w-full border rounded mb-2 p-2 bg-gray-100"
        placeholder="Broker Email (auto-filled)"
      />

      <input
        type="text"
        name="source"
        value={load.source}
        onChange={handleChange}
        className="w-full border rounded mb-2 p-2"
        placeholder="Source"
        required
      />

      <input
        type="text"
        name="destination"
        value={load.destination}
        onChange={handleChange}
        className="w-full border rounded mb-2 p-2"
        placeholder="Destination"
        required
      />

      <input
        type="text"
        name="loadType"
        value={load.loadType}
        onChange={handleChange}
        className="w-full border rounded mb-2 p-2"
        placeholder="Load Type"
      />

      <input
        type="text"
        name="weight"
        value={load.weight}
        onChange={handleChange}
        className="w-full border rounded mb-2 p-2"
        placeholder="Weight"
      />

      <input
        type="date"
        name="date"
        value={load.date}
        onChange={handleChange}
        className="w-full border rounded mb-2 p-2"
      />

      <input
        type="text"
        name="price"
        value={load.price}
        onChange={handleChange}
        className="w-full border rounded mb-2 p-2"
        placeholder="Price"
      />

      <button
        type="submit"
        className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
      >
        Post Load
      </button>
    </form>
  );
};

export default PostLoadForm;
