// src/components/PostLoad.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";

const PostLoad = () => {
  const [formData, setFormData] = useState({
    origin: "",
    destination: "",
    loadType: "",
    weight: "",
    brokerName: "",
    brokerNumber: "",
  });

  // Fetch broker info from localStorage/sessionStorage
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user")); // adjust based on your storage
    if (user?.role === "broker") {
      setFormData((prev) => ({
        ...prev,
        brokerName: user.name,
        brokerNumber: user.mobile,
      }));
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/postload", formData);
      alert("Load posted successfully!");
      setFormData({
        origin: "",
        destination: "",
        loadType: "",
        weight: "",
        brokerName: "",
        brokerNumber: "",
      });
    } catch (error) {
      console.error("Error posting load:", error);
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Post a Load</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input type="text" name="origin" value={formData.origin} onChange={handleChange} placeholder="Origin" required className="w-full border p-2 rounded" />
        <input type="text" name="destination" value={formData.destination} onChange={handleChange} placeholder="Destination" required className="w-full border p-2 rounded" />
        <input type="text" name="loadType" value={formData.loadType} onChange={handleChange} placeholder="Load Type" required className="w-full border p-2 rounded" />
        <input type="number" name="weight" value={formData.weight} onChange={handleChange} placeholder="Weight (in tons)" required className="w-full border p-2 rounded" />

        {/* Hidden Fields */}
        <input type="hidden" name="brokerName" value={formData.brokerName} />
        <input type="hidden" name="brokerNumber" value={formData.brokerNumber} />

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Post Load</button>
      </form>
    </div>
  );
};

export default PostLoad;
