import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Sun, Moon, Menu, X } from "lucide-react";

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);

  const toggleLogin = () => setIsLoggedIn(!isLoggedIn);
  const toggleDarkMode = () => setIsDarkMode(!isDarkMode);
  const toggleMobileMenu = () => setIsMobileMenuOpen(!isMobileMenuOpen);

  const navLinks = (
    <>
      <Link to="/" className="hover:text-yellow-400">Home</Link>
      <Link to="/add-vehicle" className="hover:text-yellow-400">Add Vehicle</Link>
      <Link to="/assign-driver" className="hover:text-yellow-400">Assign Driver</Link>
      <Link to="/plan-trip" className="hover:text-yellow-400">Trip Planner</Link>
      <Link to="/log-expenses" className="hover:text-yellow-400">Expense Logger</Link>
      <Link to="/invoice" className="hover:text-yellow-400">Invoice</Link>
    </>
  );

  return (
    <nav className={`px-6 py-4 shadow-lg flex justify-between items-center transition ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-white text-gray-900'}`}>
      {/* Logo */}
      <div className="text-2xl font-bold">
        <Link to="/">🚛 Lorry Tracker</Link>
      </div>

      {/* Desktop Nav */}
      <div className="hidden md:flex items-center space-x-6 text-lg">
        {navLinks}
        <button onClick={toggleDarkMode}>
          {isDarkMode ? <Sun className="w-5 h-5" /> : <Moon className="w-5 h-5" />}
        </button>

        {isLoggedIn ? (
          <div className="relative group">
            <button className="hover:text-yellow-400">Profile ▾</button>
            <div className="absolute right-0 mt-2 hidden group-hover:block bg-gray-700 text-white rounded shadow-lg w-40 z-10">
              <Link to="/profile" className="block px-4 py-2 hover:bg-gray-600">My Profile</Link>
              <button onClick={toggleLogin} className="w-full text-left px-4 py-2 hover:bg-gray-600">Logout</button>
            </div>
          </div>
        ) : (
          <button onClick={toggleLogin} className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded text-white">
            Login
          </button>
        )}
      </div>

      {/* Mobile Menu Toggle */}
      <button onClick={toggleMobileMenu} className="md:hidden">
        {isMobileMenuOpen ? <X /> : <Menu />}
      </button>

      {/* Mobile Menu */}
      {isMobileMenuOpen && (
        <div className={`absolute top-16 left-0 w-full px-6 py-4 flex flex-col space-y-4 text-lg z-50 ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-white text-gray-900'}`}>
          {navLinks}
          <button onClick={toggleDarkMode} className="text-left">
            {isDarkMode ? "☀️ Light Mode" : "🌙 Dark Mode"}
          </button>
          {isLoggedIn ? (
            <>
              <Link to="/profile">My Profile</Link>
              <button onClick={toggleLogin}>Logout</button>
            </>
          ) : (
            <button onClick={toggleLogin}>Login</button>
          )}
        </div>
      )}
    </nav>
  );
};

export default Navbar;
