import React, { useState, useEffect } from "react";
import axios from "axios";

const VehicleStatus = () => {
  const [vehicles, setVehicles] = useState([]);
  const [statusData, setStatusData] = useState({
    vehicleNumber: "",
    status: "",
  });

  useEffect(() => {
    // Fetch all vehicles to populate dropdown
    const fetchVehicles = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/vehicles");
        setVehicles(res.data);
      } catch (err) {
        console.error("Error fetching vehicles:", err);
      }
    };
    fetchVehicles();
  }, []);

  const handleChange = (e) => {
    setStatusData({ ...statusData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put("http://localhost:5000/api/vehicles/status", statusData);
      alert("Vehicle status updated successfully");
      setStatusData({ vehicleNumber: "", status: "" });
    } catch (err) {
      console.error("Error updating vehicle status:", err);
      alert("Failed to update vehicle status");
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 bg-white shadow-md p-8 rounded-lg">
      <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">
        Update Vehicle Status
      </h2>
      <form onSubmit={handleSubmit} className="space-y-5">
        <select
          name="vehicleNumber"
          value={statusData.vehicleNumber}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
          required
        >
          <option value="">Select Vehicle</option>
          {vehicles.map((v) => (
            <option key={v._id} value={v.number}>
              {v.number}
            </option>
          ))}
        </select>
        <select
          name="status"
          value={statusData.status}
          onChange={handleChange}
          className="w-full border border-gray-300 rounded-md p-3"
          required
        >
          <option value="">Select Status</option>
          <option value="Available">Available</option>
          <option value="Under Trip">Under Trip</option>
          <option value="Under Repair">Under Repair</option>
        </select>
        <button
          type="submit"
          className="bg-red-600 hover:bg-red-700 text-white w-full py-3 rounded-md font-semibold"
        >
          Update Status
        </button>
      </form>
    </div>
  );
};

export default VehicleStatus;
