import React, { useState } from "react";
import { Link, Routes, Route, Navigate, useLocation } from "react-router-dom";
import TripChecklistForm from "./TripChecklistForm";
import ExpenseLogForm from "./ExpenseLogForm";
import TripLog from "./TripLog";

const DriverDashboard = () => {
  const location = useLocation();
  const [isNavigating, setIsNavigating] = useState(false); // Prevent rapid clicks

  console.log("Current path:", location.pathname); // Debug: Log current path

  const handleLinkClick = () => {
    setIsNavigating(true);
    setTimeout(() => setIsNavigating(false), 300); // Debounce for 300ms
  };

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-6">
      <h2 className="text-3xl font-bold text-center mb-8 text-gray-800">
        Driver Dashboard
      </h2>
      <nav className="mb-8">
        <ul className="flex justify-center space-x-4">
          {[
            { label: "Trip Checklist", path: "driver-trips" },
            { label: "Trip Log", path: "trip-log" },
            { label: "Expenses Log", path: "driver-expenses" },
          ].map((item) => {
            const isActive =
              location.pathname === `/driver-dashboard/${item.path}`;
            return (
              <li key={item.path}>
                <Link
                  to={item.path}
                  replace
                  onClick={handleLinkClick}
                  className={`px-4 py-2 rounded-md text-lg font-semibold transition ${
                    isActive || isNavigating
                      ? "bg-green-500 text-white cursor-not-allowed"
                      : "bg-white text-gray-700 hover:bg-green-50"
                  }`}
                  style={
                    isNavigating || isActive ? { pointerEvents: "none" } : {}
                  }
                >
                  {item.label}
                </Link>
              </li>
            );
          })}
        </ul>
      </nav>
      <div className="max-w-4xl mx-auto">
        <Routes>
          <Route path="/" element={<Navigate to="driver-trips" replace />} />
          <Route path="driver-trips" element={<TripChecklistForm />} />
          <Route path="driver-expenses" element={<ExpenseLogForm />} />
          <Route path="trip-log" element={<TripLog />} />
          <Route path="*" element={<Navigate to="driver-trips" replace />} />
        </Routes>
      </div>
    </div>
  );
};

export default DriverDashboard;
