import React, { useEffect, useState } from "react";
import { getPostedLoadsByBroker, deleteLoad } from "../services/api";

const OwnerDashboard = () => {
  const [loads, setLoads] = useState([]);
  const brokerId = JSON.parse(localStorage.getItem("user"))?._id;

  useEffect(() => {
    if (brokerId) {
      getPostedLoadsByBroker(brokerId)
        .then((res) => setLoads(res.data))
        .catch((err) => console.error("Error fetching loads:", err));
    }
  }, [brokerId]);

  const handleDelete = async (id) => {
    try {
      await deleteLoad(id);
      setLoads((prev) => prev.filter((load) => load._id !== id));
    } catch (err) {
      console.error("Error deleting load:", err);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">My Posted Loads</h1>
      <table className="min-w-full bg-white shadow-md rounded-lg overflow-hidden">
        <thead>
          <tr>
            <th className="px-4 py-2">Origin</th>
            <th className="px-4 py-2">Destination</th>
            <th className="px-4 py-2">Amount</th>
            <th className="px-4 py-2">Status</th>
            <th className="px-4 py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {loads.map((load) => (
            <tr key={load._id} className="border-b">
              <td className="px-4 py-2">{load.origin}</td>
              <td className="px-4 py-2">{load.destination}</td>
              <td className="px-4 py-2">â‚¹{load.amount}</td>
              <td className="px-4 py-2">
                <span
                  className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                    load.status === "available"
                      ? "bg-green-100 text-green-800"
                      : load.status === "booked"
                      ? "bg-yellow-100 text-yellow-800"
                      : "bg-gray-100 text-gray-800"
                  }`}
                >
                  {load.status || "unknown"}
                </span>
              </td>
              <td className="px-4 py-2">
                <button
                  onClick={() => handleDelete(load._id)}
                  className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OwnerDashboard;
