import React from "react";
import { Link } from "react-router-dom";

const Dashboard = () => {
  return (
    <div className="min-h-screen bg-gray-100 py-10 px-6">
      <h2 className="text-3xl font-bold text-center mb-8 text-gray-800">
        LorryTracker Dashboard
      </h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 max-w-6xl mx-auto">
        {[
          { label: "Add Vehicle", path: "/add-vehicle" },
          { label: "Assign Driver", path: "/assign-driver" },
          { label: "Plan Trip", path: "/trip-planner" },
          { label: "Log Expenses", path: "/log-expense" },
          { label: "Generate Invoice", path: "/invoice" },
        ].map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className="bg-white shadow-md hover:shadow-xl rounded-xl p-6 text-center text-lg font-semibold text-gray-700 hover:bg-red-50 transition"
          >
            {item.label}
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
